import 'dart:math';

var rand = new Random();

// 返回一个大于等于 0.0　小于
//  1.0 的随机 double
rand.nextDouble();

// 返回一个随机的布尔值
rand.nextBool();

// 返回一个大于等于 0
// 小于 10 的随机整数
rand.nextInt(10);

